mapper.select:




SELECT BID,BNAME,BTITLE,BCONTENT,BDATE,
BHIT,BGROUP,BSTEP,BINDENT  FROM
(SELECT ROWNUM NUM,N.* FROM (SELECT * FROM REPLYBOARD ORDER BY BGROUP DESC, BSTEP ASC) N

) 
WHERE NUM BETWEEN #{param1} AND #{param2}

SELECT BID,BNAME,BTITLE,BCONTENT
	,BDATE,BHIT,BGROUP,BSTEP,BINDENT,FILESRC
	FROM REPLYBOARD WHERE BID=#{param1}

mapper.update:
UPDATE REPLYBOARD 
	SET BNAME=#{param2}, BTITLE=#{param3},
	BCONTENT=#{param4}	WHERE BID=#{param1}

UPDATE REPLYBOARD
	SET BHIT=BHIT+1
	WHERE BID=#{param1}

mapper.select:
SELECT BID,BNAME,BTITLE,BCONTENT
,BDATE,BHIT,BGROUP,BSTEP,BINDENT
 FROM REPLYBOARD WHERE BID=#{param1}

SELECT BGROUP, BSTEP,BINDENT 
	FROM REPLYBOARD WHERE BGROUP=#{param1}
	ORDER BY BSTEP ASC
