mapper.select:

SELECT * FROM
(SELECT ROWNUM NUM,N.* FROM (SELECT * FROM REPLYBOARD ORDER BY
BGROUP DESC,BSTEP ASC) N

)
WHERE NUM BETWEEN #{param1} AND #{param2}

SELECT BID,BNAME,BTITLE,BCONTENT,BDATE,
BHIT,BGROUP,BSTEP,BINDENT,FILESRC
FROM REPLYBOARD WHERE BID=#{param1}

mapper.update:
UPDATE REPLYBOARD
SET BHIT=BHIT+1
WHERE BID=#{param1}

UPDATE REPLYBOARD
SET BSTEP=BSTEP+1
WHERE BGROUP=#{param1} AND BSTEP>#{param2}
